pageContext.rootQuery:
query getPortfolioItem($uid: String!) {
  prismic {
    portfolio_item(uid: $uid, lang: "en-us") {
      title
      excerpt
      subtitle
      website
      url {
        ... on PRISMIC__ExternalLink {
          _linkType
          url
        }
      }  
      client
      role
      type {
        service {
          ... on PRISMIC_Services_item {
            title
            _meta {
              uid
            }
          }
        }
      }
      introduction_subtitle
      introduction_text
      introduction_title
      introduction_images {
        introduction_image 
        introduction_imageSharp{
          childImageSharp {
            fluid(maxWidth: 960) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
        }          
      }
      main_image
      main_imageSharp {
          childImageSharp {
            fluid(quality: 100, maxWidth: 960) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
      }
      body {
        ... on PRISMIC_Portfolio_itemBodyText_section {
          type
          label
          fields {
            text_block_subtitle
            text_block_text
            text_block_title
          }
        }
        ... on PRISMIC_Portfolio_itemBodyQuote {
          type
          label
          fields {
            quote_text
          }
        }
      }
    }
  }
}
